CREATE PROCEDURE SP_REPORT_KEYS_ACQUIRER_EQUIP  
(  
@MODEL VARCHAR(100) = null,  
@ACQUIRER VARCHAR(100) = null  
)  
AS  
DECLARE @QUERY NVARCHAR(MAX)  
BEGIN  
  
SET @QUERY = '  
WITH CTE   
AS  
 (  
SELECT   
EQUIPMENT.SERIAL,  
ACQUIRER_GROUP.NAME_AC,  
EQUIPMENT_MODEL.CODIGO AS MODEL,  
ISNULL(VW_COMPANY_EC_AFF_BR_DEP_EQUIP.EC, '''') AS MERCHANT,  
ISNULL(VW_COMPANY_EC_AFF_BR_DEP_EQUIP.CPF_CNPJ_EC, '''') AS MERCHANT_DOC,  
ISNULL(VW_COMPANY_EC_AFF_BR_DEP_EQUIP.AFFILIATOR, '''') AS AFFILIATOR,  
COUNT(*) AS QTY,  
ACQUIRER_KEY_EQUIPMENT.ACTIVE  
FROM ACQUIRER_KEY_EQUIPMENT  
JOIN KEYMAP_DATA ON KEYMAP_DATA.COD_KEY_MAP = ACQUIRER_KEY_EQUIPMENT.COD_KEY_MAP  
JOIN ACQUIRER_GROUP ON ACQUIRER_GROUP.COD_AC_GP = KEYMAP_DATA.COD_AC_GP  
JOIN EQUIPMENT ON EQUIPMENT.COD_EQUIP = ACQUIRER_KEY_EQUIPMENT.COD_EQUIP  
JOIN EQUIPMENT_MODEL ON EQUIPMENT_MODEL.COD_MODEL = EQUIPMENT.COD_MODEL  
LEFT JOIN VW_COMPANY_EC_AFF_BR_DEP_EQUIP ON VW_COMPANY_EC_AFF_BR_DEP_EQUIP.COD_EQUIP = EQUIPMENT.COD_EQUIP  
GROUP BY   
EQUIPMENT.SERIAL,  
EQUIPMENT_MODEL.CODIGO,  
ACQUIRER_GROUP.NAME_AC,  
ISNULL(VW_COMPANY_EC_AFF_BR_DEP_EQUIP.EC, ''''),  
ISNULL(VW_COMPANY_EC_AFF_BR_DEP_EQUIP.CPF_CNPJ_EC, ''''),  
ISNULL(VW_COMPANY_EC_AFF_BR_DEP_EQUIP.AFFILIATOR, '''') ,  
ACQUIRER_KEY_EQUIPMENT.ACTIVE  
)  
SELECT * FROM CTE WHERE CTE.ACTIVE = 1 '  
  
IF @MODEL IS NOT NULL  
SET @QUERY = @QUERY + ' AND MODEL = @MODEL '  
  
IF @ACQUIRER IS NOT NULL  
SET @QUERY = @QUERY + ' AND NAME_AC = @ACQUIRER '  
  
  
  
EXEC [sp_executesql] @QUERY    
       ,N'                  
    @MODEL VARCHAR(100),                  
    @ACQUIRER VARCHAR(100)          
   '    
       ,@MODEL= @MODEL  
       ,@ACQUIRER = @ACQUIRER   
  
  
END;  
