        SET @String = REPLACE(REPLACE(REPLACE(REPLACE(@String,'ú','u'),'ù','u'),'û','u'),'ü','u')

        /****************************************************************************************************************/
        /** RETIRA ACENTUAÇÃO DAS CONSOANTES **/
        /****************************************************************************************************************/
        SET @String = REPLACE(@String,'ý','y')
        SET @String = REPLACE(@String,'ñ','n')
        SET @String = REPLACE(@String,'ç','c')

        RETURN UPPER(@String)

    END
